.gloss-sc-table[data-sc-class="img"] .gloss-image-container-overlay {
    max-width: 70% !important;
}

@media (max-width: 500px) {
	.gloss-sc-table[data-sc-class="img"] .gloss-image-container,
	.gloss-sc-table[data-sc-class="img"] .gloss-image-container-overlay {
		max-width: 65% !important;
	}
}

