        {{~/regexReplace~}}{{/set~}}
        ラノベ::{{~get "ln-title"~}}
    {{~else if (regexMatch "mokuro" "" context.document.title)~}}
        {{~#set "manga-title" ~}}{{~#regexReplace " |　" "_"~}}
            {{~#regexMatch ".+?(?= \| mokuro)"~}}{{~context.document.title~}}{{~/regexMatch~}}
        {{~/regexReplace~}}{{/set~}}
        漫画::{{~get "manga-title"~}}
    {{~else if (regexMatch "nhk.or.jp" "" definition.url)~}}
